draw((0,0)--(0,5)--(1,5)--(1,2)--(4,2)--(4,0)--cycle);
draw((0,1)--(4,1));
draw((0,2)--(1,2));
draw((0,3)--(1,3));
draw((0,4)--(1,4));
draw((1,0)--(1,2));
draw((2,0)--(2,2));
draw((3,0)--(3,2));
