k=(1/2)*E+(1/2)*D;
l=(1/4)*E+(3/4)*D;

draw(a--m--j);
draw(b--n--k);
draw(c--o--l);

f=(3/4)*G+(1/4)*B;
e=(1/2)*G+(1/2)*B;
d=(1/4)*G+(3/4)*B;

r=(3/4)*D+(1/4)*A;
q=(1/2)*D+(1/2)*A;
