draw(O--2*I, Arrow3(6));
draw((-2)*J--2*J, Arrow3(6));
draw(O--2*K, Arrow3(6));
draw(O--P);
draw(O--Q);
draw(O--R);
draw(O--S);
draw(O--T);
draw(O--U);
draw(P--Q--R--S--T--U,dashed);

