pair A, B, C, D, E, F, H;

A = (1,3);
B = (0,0);
C = (4,0);
D = (A + reflect(B,C)*(A))/2;
E = (B + reflect(C,A)*(B))/2;
F = (C + reflect(A,B)*(C))/2;
H = extension(B,E,C,F);

draw(A--B--C--cycle);
draw(A--D);
draw(B--E);
draw(C--F);

label("$A$", A, N);
label("$B$", B, SW);
label("$C$", C, SE);
label("$D$", D, S);
label("$E$", E, NE);
