for (i = 1; i <= 8; ++i) {
  draw((i,-0.1)--(i,0.1));
}

draw((-0.1,3)--(0.1,3));
draw((-0.1,-3)--(0.1,-3));

label("$y = f(x)$", (8.5,-2), red);
label("$y = g(x)$", (6,3), E, blue);
