label("$A$",A,NW);
label("$B$",B,E);
label("$C$",C,SW);
label("$P$",P,E);
label("$D$",(A+C)/2,W);
label("$E$",(A+B)/2,NE);
draw((0,0.4)--(0.4,0.4)--(0.4,0));[/asy]

