pair[] dotted = {A,B,C,D,E,F,G,H,M,N};
D(A--B--C--G--H--E--A);
D(E--F--B);
D(F--G);
pathpen=dashed;
D(A--D--H);
D(D--C);
dot(dotted);
label("$A$",A,SW);
label("$B$",B,S);
label("$C$",C,SE);
label("$D$",D,NW);
label("$E$",E,W);
label("$F$",F,SE);
label("$G$",G,NE);
label("$H$",H,NW);
