draw((-10,0)--(-5,8.7)--(5,8.7)--(10,0)--cycle);
draw((-5,8.7)--(0,0)--(5,8.7));
draw(anglemark((-5,8.7),(0,0),(-10,0),30));
draw(anglemark((5,8.7),(0,0),(-5,8.7),35));
draw(anglemark((10,0),(0,0),(5,8.7),30));
[/asy]

