unitsize(1 cm);

pair A, O, W;

A = (-5,0);
O = (0,0);
W = (-24/5,2*sqrt(6)/5);

draw((-7,0)--(1,0));
draw((0,-1)--(0,1));
draw(Circle(A,1));
draw(A--W--O);
draw(rightanglemark(A,W,O,6));

label("$O$", O, SE);
label("$W$", W, N);

