B = (0,0);
C = (6,0);
D = (B + C)/2;
E = (A + C)/2;
F = (A + B)/2;
P = (A + D)/2;
Q = (B + E)/2;
R = (C + F)/2;

draw(A--B--C--cycle);
draw(A--D);
draw(B--E);
draw(C--F);

label("$A$", A, N);
label("$B$", B, SW);
