pair A, B, C, D, E, F, H;

B = (0,0);
C = (8,0);
A = intersectionpoint(arc(B,5,0,180),arc(C,7,0,180));
H = orthocenter(A,B,C);
D = (A + reflect(B,C)*(A))/2;
E = (B + reflect(C,A)*(B))/2;
F = (C + reflect(A,B)*(C))/2;

draw(A--B--C--cycle);
draw(A--D,dashed);
draw(B--E,dashed);
