pair A, B, C, D, E, P;

B = (0,0);
C = (3,0);
A = intersectionpoint(arc(B,7,0,180),arc(C,5,0,180));
D = extension(A,incenter(A,B,C),B,C);
E = extension(B,incenter(A,B,C),A,C);
