real g(real x) {return -x^3+2x^2+3x;}

real x;

draw(graph(f,-15,3),BeginArrow(4));
draw(graph(g,3,4),EndArrow(4));

real eps = 0.2;

draw((-eps,3)--(eps,3));
draw((-eps,0)--(eps,0));
draw((-eps,-3)--(eps,-3));

dot("$(-3,6)$",(-3,6),SW);
dot("$(3,0)$",(3,0),NE);

label("$f(x)$",(3,20.5));
label("$x$",(20.5,-1));
[/asy]

question:
