draw((0,0)--Z);
draw((0,0)--P);
draw(rightanglemark((0,0),P,(4,0),8));

dot("$4$", (4,0), S);
dot("$3i$", (0,3), W);
dot("$z$", Z, NE);

label("$h$", P/2, NW);
