draw(Circle((10,0),2));
dot((0,0));dot((10,0));
label("$O$",(0,0),SW); label("$P$",(10,0),SW);
dot((8,0)); label("$Q$",(8,0),SW);
label("$T$",(4.6,6.6),NE); label("$S$",(11,1.7),NE);
