path ell = xscale(5)*yscale(3)*Circle((0,0),1);
pair A, B, C, D, F, O;

A = (5,0);
B = (-5,0);
C = (0,3);
D = (0,-3);
F = (4,0);
O = (0,0);

draw(ell);
draw(A--B);
