[asy]
real x = sqrt(2);
pair A,B,C,D,E,F,G,H;
F=(0,0); E=(2,0); D=(2+x,x); C=(2+x,2+x);
B=(2,2+2x); A=(0,2+2x); H=(-x,2+x); G=(-x,x);
draw(A--B--C--D--E--F--G--H--cycle);
