triple P = (0,2,0), Q = (1,0,0), R = (1,4,4), S = lineintersectplan(B, F - B, P, cross(P - Q, P - R)), T = lineintersectplan(C, D - C, P, cross(P - Q, P - R));

draw(C--G--E--F--B--D--cycle);
draw(F--H);
draw(D--H);
draw(G--H);
