N=(5D+F)/6;
P=(2C+B)/3;
Q=(2A+C)/3;
R=(6,-38);
draw(A--B--C--A--E--D--F--B--C--D);
draw(M--N--P--Q--M, dashed);
draw(D--R);
draw(M--R, dashed);
label("$A$", A, NW);
