draw((0,1,1)--(1,0,1)--(1,1,0)--cycle);

draw((0,0,0)--(1,0,0),dashed);
draw((0,0,0)--(0,1,0),dashed);
draw((0,0,0)--(0,0,1),dashed);
draw((1,0,0)--(1.2,0,0),Arrow3(6));
draw((0,1,0)--(0,1.2,0),Arrow3(6));
draw((0,0,1)--(0,0,1.2),Arrow3(6));
draw((0,0,0)--(2/3,2/3,2/3),dashed);

label("$x$", (1.3,0,0));
label("$y$", (0,1.3,0));
label("$z$", (0,0,1.3));
label("$(0,0,0)$", (0,0,0), W, fontsize(10));
label("$(1,0,0)$", (1,0,0), NW, fontsize(10));
label("$(0,1,0)$", (0,1,0), NE, fontsize(10));
label("$(0,0,1)$", (0,0,1), NW, fontsize(10));
label("$(1,1,0)$", (1,1,0), S, fontsize(10));
label("$(1,0,1)$", (1,0,1), NW, fontsize(10));
label("$(0,1,1)$", (0,1,1), NE, fontsize(10));

dot("$(\frac{2}{3}, \frac{2}{3}, \frac{2}{3})$", (2/3,2/3,2/3), NE, fontsize(10));
