  real r = 9/(5 - 4*cos(t));
  return (r*cos(t), r*sin(t));
}

path foo = moo(0);
real t;

for (t = 0; t <= 2*pi + 0.01; t = t + 0.01) {
  foo = foo--moo(t);
}

draw(foo,red);
