pair pA, pB, pC, pD, pE, pF;
pA = (-2, 0);
pB = (0, 4.5826);
pC = (2, 0);
pD = (pB * 4 + pC * 5) / (9);
pE = (0, 0);
pF = (pE * 4 + pC * 5) / (9);
draw(pA--pB--pC--pA);
draw(pA--pD);
draw(pB--pE);
