F = (0,0);
G = (1,0);
D = (1,1);
E = (0,1);
H = (E + F)/2;
A = reflect(D,H)*(G);
B = reflect(D,H)*(F);
C = reflect(D,H)*(E);

draw(A--B--C--D--cycle);
draw(D--E--F--G--cycle);

