A = (44/5,33/5);
B = (0,0);
C = (20,0);
D = (B + C)/2;
E = (C + A)/2;
F = (A + B)/2;
G = (A + B + C)/3;
P = (G + reflect(B,C)*(G))/2;
Q = (A + reflect(B,C)*(A))/2;
draw(A--B--C--cycle);
draw(A--D);
draw(B--E);
draw(C--F);
draw(G--P);
