draw((0,0)--(0,5));
filldraw((1,0)--(1,1)--(2,1)--(2,0)--cycle,gray(0.7));
filldraw((2,0)--(2,2)--(3,2)--(3,0)--cycle,gray(0.7));
filldraw((5,0)--(5,5)--(6,5)--(6,0)--cycle,gray(0.7));
