axes(-8,8, -6, 12);
yh(2*sqrt(3),sqrt(3),-1,3,-5,3);
dot((0,7));
dot((-1,3));
real f(real x) { return 2*x+5; }
real g(real x) { return -2*x+1; }
draw(graph(f, -5, 3) ^^ graph(g, -5, 3),dotted);
[/asy]

