E[1] = rotate(60)*(D[1]);
E[2] = rotate(-60)*(D[1]);

draw(A--B--C--cycle);
draw(A--D[1]--E[1]--cycle);
draw(A--E[2]--D[1]);
draw(Circle(B,sqrt(11)),dashed);
