A = (1,2);
B = (0,0);
C = (3,0);
I = incenter(A,B,C);
D = (I + reflect(B,C)*(I))/2;
E = (I + reflect(C,A)*(I))/2;
F = (I + reflect(A,B)*(I))/2;
draw(A--B--C--cycle);
draw(incircle(A,B,C));
draw(E--I--F);
draw(A--I);
