A[3] = (10,0);
A[4] = (0,0);
P[1] = (A[1] + A[2])/2;
P[2] = (A[3] + A[4])/2;

B[1] = P[1] + 4*dir(-45);
B[4] = B[1] + P[2] - P[1];
