pair A, B, C, D, E, F, H;

A = (2,5);
B = (0,0);
C = (8,0);
D = (A + reflect(B,C)*(A))/2;
E = (B + reflect(C,A)*(B))/2;
F = (C + reflect(A,B)*(C))/2;

