pair A, B, O, P, T;
T = dir(70);
P = T + dir(-20);
B = dir(150);
O = (0,0);
A = intersectionpoint(P--interp(P,B,0.9),Circle(O,1));
draw(Circle(O,1));
draw(T--P--B);
label("$A$", A, SW);
label("$B$", B, NW);
dot("$O$", O, S);
