label("$B$",(2,0),E);
label("1",(-1.5,0),S);
label("1",(-0.5,0),S);
label("1",(0.5,0),S);
draw((-1.5,0.87)--(-1,0)--(-0.5,0.87)--(0,0)--(0.5,0.87)--(1,0)--(1.5,0.87),linewidth(0.7));
label("1",(1.5,0),S);
dot((0,0));
dot((-1,0));
dot((1,0));
draw((-2,-0.1)--(-2,-0.4));
draw((-1,-0.1)--(-1,-0.4));
draw((2,-0.1)--(2,-0.4));
draw((1,-0.1)--(1,-0.4));
draw((0,-0.1)--(0,-0.4));
[/asy]

