A = (0,12);
B = (0,0);
C = (9,0);
D = (4,0);

draw(A--B--C--cycle);
draw(A--D);

label("$A$", A, NW);
label("$B$", B, SW);
label("$C$", C, SE);
label("$D$", D, S);
label("$4$", (B + D)/2, S);
label("$12$", (A + B)/2, W);
label("$x$", (C + D)/2, S);
label("$3x$", (A + C)/2, NE);
