triple I = (1,0,0), J = (0,1,0), K = (0,0,1), O = (0,0,0);
triple V = (2.2,2.5,1.5), W = (0,2.5,0);

draw(V--W,dashed);
draw(O--V, red, Arrow3(6));
draw(O--W,blue, Arrow3(6));
draw(O--3*I, Arrow3(6));
draw(2.5*J--3*J, Arrow3(6));
draw(O--3*K, Arrow3(6));

