triple A, B, Bp, C, D, E;
real t;

A = (sqrt(3),1,-1);
B = (sqrt(3),-1,-1);
Bp = (sqrt(3),1,1);
C = (sqrt(3),-1,1);
D = (0,0,1);
E = (0,0,-1);

path3 circ = (2,0,-1);
for (t = 0; t <= 2*pi + 0.1; t = t + 0.1) {
  circ = circ--((0,0,-1) + (2*cos(t),2*sin(t),0));
}

draw(circ);
draw(surface(A--B--C--Bp--cycle),paleyellow,nolight);

path3 circ = (2,0,1);
for (t = 0; t <= 2*pi + 0.1; t = t + 0.1) {
  circ = circ--((0,0,1) + (2*cos(t),2*sin(t),0));
}

draw(circ);
draw(C--D--E--A);
draw(A--B--C--Bp--cycle);

dot("$A$", A, S);
dot("$B_1$", B, W);
dot("$B_2$", Bp, N);
dot("$C$", C, W);
dot("$D$", D, NE);
dot("$E$", E, dir(0));
