draw((2,0)--(4,4));

label("4",(4,4),N);

draw((4,0)--(2,4));
label("$3$",(2,4),NE);

draw((5.5,4)--(0,1.5));
label("$1$",(0,1.5),W);

draw((0,3)--(5,0));
label("$2$",(0,3), NW);

[/asy]

