size(5cm,5cm);

draw((0,0)--(5,0)--(5,7)--(0,7)--(0,0));

draw((1,1)--(4,1)--(4,6)--(1,6)--(1,1));

fill(((1,1)--(4,1)--(4,6)--(1,6)--cycle),darkblue);

