draw (shift(0, -0.5)*((4,0)--(5,0)), Bars);

label("$1''$",(4.5,-0.5),S);

draw (shift(0.5,0)*((5,0)--(5,1)), Bars);

label("$1''$",(5.5,0.5),E);

