defaultpen(linewidth(0.7pt)+fontsize(11pt));
dotfactor=3;
pair A=(8,0), B=(0,5), C=(-8,0), D=(0,-5), Ep = (0,0);
draw(A--B--C--D--cycle);
draw(A--C);
draw(B--D);
label("$x$",midpoint(Ep--B),W);
label("$x+3$",midpoint(Ep--A),S);
label("$\sqrt{89}$",midpoint(A--B),NE);[/asy]

