C = (4,0);
D = (A + reflect(B,C)*(A))/2;
E = (B + reflect(C,A)*(B))/2;
F = (C + reflect(A,B)*(C))/2;
H = extension(B,E,C,F);

draw(A--B--C--cycle);
draw(A--D);
draw(B--E);
draw(C--F);

